import React, {useState, useEffect} from 'react';
import './Post.css'



function Post({post}){

    const [imageUrl, setImageUrl] = useState('')

    useEffect(() => {
        if(post.image_url_type == 'absolute'){
            setImageUrl(post.ima)
        }
    },[])

    return (
        <div className='post'>
            <img
                className="post_image"
                src = {imageUrl}
             />
        </div>
    )
}

export default Post
