import React, {useState, useEffect} from 'react';
import './App.css';
import Post from './Post'
import {Avatar, Button, Modal, makeStyles, Input} from "@material-ui/core"

const BASE_URL ='http://localhost:8000/'


function getModalStyle() {
  const top=50;
  const left=50;

  return {
    top: `${top}%`,
    left: `${top}%`,
    transform : `translate(-${top}%, -${left}%)`,
  };
}

const useStyles = makeStyles((theme) => ({
    paper: {
      backgroundColor: theme.palette.background.paper,
      position: 'absolute',
      width: 400,
      border: '2px solid #000',
      boxShadow: theme.shadows[5],
      padding: theme.spacing(2,4,3)
    }
}))



function App() {

  const classes = useStyles();

  const [posts, setPosts] = useState([]);
  const [openSignIn,setOpenSignIn] =useState(false);
  const [openSignUp,setOpenSignUp] =useState(false);
  const [modalStyle,setModalStyle] =useState(getModalStyle);
  const [username,setUsername] = useState('')
  const [password,setPassword] = useState('')

  useEffect(() => {
      fetch(BASE_URL + 'post/all')
        .then(response => {
            const json = response.json();
            console.log(json);
            if(response.ok){
              return json
            }
            throw response
        })
        .then(data =>{
          const result= data.sort((a,b) => {
            const t_a = a.Timestamp.split(/[-T:]/);
            const t_b = b.Timestamp.split(/[-T:]/);
            const d_a = new Date(Date.UTC(t_a[0], t_a[1]-1, t_a[2], t_a[3], t_a[4], t_a[5] ));
            const d_b = new Date(Date.UTC(t_b[0], t_b[1]-1, t_b[2], t_b[3], t_b[4], t_b[5] ));
            return d_b- d_a;
          })
          return result
        })
        .then(data =>{
            setPosts(data)
        })
        .catch(error =>{
          console.log(error);
          alert(error)
        })

  },[])

  const signIn = (event) =>{
    event.preventDefault();

    let formData = new FormData();
    formData.append('username', username);
    formData.append('password', password);

    const requestOptions = {
      method: 'POST',
      body : formData
    }

    fetch(BASE_URL+'login', requestOptions)
      .then(response => {
        if (response.ok){
          return response.json()
        }
        throw response
      })
      .then(data =>{
        console.log(data);
      })
  }

  return (
    <div className='app'>

      <Modal open={openSignIn} onClose= {() => setOpenSignIn(false)}>

        <div style={modalStyle} className={classes.paper}>
          <form className='app_signin'>
            <center>
              <img className="app_headerImage"
                src='https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSKDo91NDgtvxVXUsti8VQ17zrdu2RnoaXJyQ&usqp=CAU'
                alt='Instagram'/>
            </center>
            <Input
              placeholder='username'
              type='text'
              value={username}
              onChange={(e) => setUsername(e.target.value)}/>
            <Input
              placeholder='password'
              type='password'
              value={password}
              onChange={(e) => setPassword(e.target.value)}/>
            <Button
              type='submit'
              onClick={signIn}>Login</Button>
          </form>
        </div>



      </Modal>

      <div className="app_header">
        <img className="app_headerImage"
          src='https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSKDo91NDgtvxVXUsti8VQ17zrdu2RnoaXJyQ&usqp=CAU'
          alt='Instagram'
        />

        <div>
          <Button onClick = {() => setOpenSignIn(true)}>Login</Button>
          <Button onClick = {() => setOpenSignUp(true)}>Sign Up</Button>
        </div>
      </div>

      <div className="app_posts">
        {
          posts.map(post => (
            <Post
              post ={post}
            />
          ))
        }
      </div>  
    </div>
  );
}

export default App;
